insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (22, '北京溪亚宏教育科技有限公司', '20-99人', '股份制企业', 'https://company.zhaopin.com/CZ826261140.htm', '石家庄-长安区', '光华路与建华北大街交叉口百川大厦', '员工旅游，节日福利，出国游，五险一金，绩效奖金', '北京溪亚宏教育始创于2013年，中国十大培训机构之一，2016年7月6日起，溪亚宏培训机构从线下培训模式全面转型为互联网教育模式，成为在线教育最具竞争力品牌，是一家极具使命感的全方位培训机构,提供各种职业培训和就业指导，涵括工程、消防类、房产类、考研类、教师资格证类、财经类、医学类、学历类等。凭借着优越的师资团队，高端的网络技术服务平台，稳步上升的学员通过率和学员的良好口碑，快速发展跃居成为全国培训行业的佼佼者。2013年，前身：消防重点单位,为消防武警单位做内部委培，同年又获评"北京重点单位中的满意单位"。2014年，成为北京消防协会会员。2015年，年度培训人次超越万人。主做面授教学，结合全国师资进行高优质服务。2016年，成为中国建设教育协会、北京市建设教育协会会员2016年，7月获评"中国十大职业资格培训机构"、"中国十大品牌职业教育机构"等称号，同时在本年度八月份成立在线教育平台，溪亚宏北京第一呼叫中心。2018年，年初成立了石家庄第二呼叫中心，进一步展开业务需求，为更多学子提供更大的学习平台。', '');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (23, '常州市建联钢筋接头有限公司', '20-99人', '不限', 'https://company.zhaopin.com/CZ422568320.htm', '常州', '武进区', '五险一金，包吃，包住，年底双薪', '本公司位于中国江苏省常州市武进区，沪宁高速公路横山道口，始建于1987年4月，是一家专业生产钢筋连接机器和套筒的生产厂家，也是我国首家生产钢筋连接机器和套筒的工厂，并且我司很荣幸参与了中国第一个江苏省省标和中国国家机械连接标准的制定工作。我司经历改制过程后，于2003年开始从事出口贸易，现生产的产品已销售到五大洲23个国家。公司除了在制造领域获得非常大的进步外，并在工程领域也得到了发展。www.jbcz.com', '');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (24, '江苏乐商信息科技有限公司', '20-99人', '民营', 'https://company.zhaopin.com/CZ375745180.htm', '南京', '南京花神庙10号花神科技园', '五险一金，绩效奖金，弹性工作，带薪年假，通讯补助', '江苏乐商信息科技有限公司（简称LecShop）成立于2016年，注册资本1000万，是国内电商系统及服务提供的领导品牌。公司创业人全部来自于国内著名的电子商务平台苏宁易购。LecShop秉承着“促进传统企业电商转型”的理念，专注于电子商务软件的研发，为传统企业及平台电商提供电子商务系统和解决方案及相关增值服务。LecShop作为中国电子商务软件及服务的行业领跑者，凭借优质的服务、创新进取的精神、专业的团队倍受客户青睐。创立至今，已经获得"双软认证"软件企业、"双软认证"软件产品殊荣。LecShop在优化自身产品与服务的同时，不断加强与"淘宝、支付宝、财付通、腾讯微信、百度、阿里云、蚂蚁金服、万网、中国移动、Dell、Intel等众多业内知名企业合作', '');
