insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (61, '南京朝光电子科技有限公司', '20-99人', '股份制企业', 'https://company.zhaopin.com/CZ532563320.htm', '南京', '江宁区双龙大道918号麦田双龙创业园A座4层', '五险，周末双休，节日福利，员工旅游，餐补', '南京朝光电子科技有限公司（Nanjing ZG Networks Technology CO.,Ltd.），是一家专业从事智能化系统集成，网络产品、视频会议、监控系统集成及研发于一体的整体解决方案供应商和服务商。朝光高科总部设在南京，在华东区设立了无锡、常州、南通、徐州及上海5个分支服务平台，通过我们一流的技术和完善的技术服务网络，为客户和合作伙伴提供一流的服务与支持。经过朝光团队的精心营造和用心积累，朝光高科凝聚了一批具有信息技术开发与IT集成背景的高素质人才，组建了一支30余人的软硬件研发团队和一支近20人的专业销售技术服务团队，专注于IT信息化综合系统及技术自主研发和创新。南京朝光电子科技有限公司将通过不变的大客户式服务，在信息化领域致力成为业界最高性价产品的整体方案供应商和服务商；通过持续的自主技术创新，致力成为信息综合管理领域的国内知名中型企业，为价值客户持续提供一流技术和产品解决方案。公司下设：市场部，研发部，工程部，商务部，售后服务部和财务部，目前公司在政府，企业，教育，医疗等行业取得了一定的成绩，期待各路英才的加盟，共创美好前景，实现公司与个人的共同发展。', '');
