insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (248, '天津滨海新区宏泰实业集团有限公司', '500-999人', '民营', 'https://company.zhaopin.com/CZ611225420.htm', '天津-滨海新区', '天津市经济技术开发区第十大街100号', '', '天津滨海新区宏泰实业集团有限公司简介天津滨海新区宏泰实业集团有限公司是一家以工业地产开发、投资、金融、厂房办公楼租赁为主的大型集团公司，具有独立的法人资格，于1998年6月25日在天津市滨海新区工商局注册成立，地址位于天津开发区第十大街100号，注册资本1亿元人民币，占地20万平方米，有厂房建筑面积11万平方米，员工200余名。公司自成立以来，坚持“以人为本、讲求诚信，合作共赢，永续发展”的经营思路，植根滨海，成功开发了宏泰商城、开发区四大街一期项目、开发区五大街的二期、三期通用厂房项目、宏泰工业园区项目。宏泰集团是首家响应开发区管委会号召，进入微利通用厂房开发的民营企业。目前，企业提供的工业通用厂房占开发区总额的三分之一，为开发区招商引资平台搭建提供有力支持。2003年开发的宏泰工业园通用厂房项目，是开发区较大规模的工业通用厂房园区；正在建设的宁河宏泰工业园项目，以绿色和生态为主题，将成为宁河的工业园区示范项目。与此同时，公司执行多元化经营战略，跻身金融服务业。2004年投资成立天津永信投资担保有限责任公司，是滨海新区较早的专业化民营投资担保公司，是天津市及周边中小企业融资担保重要渠道。集团在经营中一方面强化聘用、留用、考核、激励、人力资源管理，引进人才，构筑中高层管理梯队；另一方完善规章制度，规范的业务流程，强化财务控制，不断提升服务功能和服务水平，使企业走上了管理规范化、制度化之路，为公司健康持续发展奠定了良好的基础。在日常管理中，按照公司制定《绩效考核管理办法》、《目标责任书》和各项规章制度，严格考核，严格执行；坚持按照操作规程进行工作；坚持按照《员工手册》强化企业责任、规范员工日常行为，发现问题及时纠正或处理，督促整改；对管理和各项工作中出现的漏洞及不良倾向，及时运用科学方法，坚持教育与处罚相结合，实现了奖罚分明，鼓励先进，鞭策落后，有力地保证和促进了公司各项工作的正常开展。经过十几年的发展，宏泰集团在天津开发区内已具有较强的发展力与影响力，形成了资源优势、资金优势、管理优势、人才优势、品牌优势、客户优势。公司地址：天津经济技术开发区第十大街100号公司网址：www.hongtaishiye.com子公司：天津永信投资担保有限公司天津永信投资担保有限责任公司2004年3月在天津开发区登记成立，注册资本9900万元人民币。公司由天津开发区宏泰实业发展有限公司、天津宏晔商贸有限公司、沃尔德（天津）房地产有限公司、自然人股东共同出资组建，是天津市滨海新区的唯一一家专业化民营投资担保公司，是天津市及周边中小企业融资担保的主渠道，2008年联合信用评定的公司信用等级为A-。2005年公司获得了天津市建交中心的资质认证，具备从事工程项下担保业务资质，我公司开具的保函与银行开具的银行保函具有同等法律效力。公司以解决中小企业融资难为己任，采用担保业务与投资业务相结合、资产抵押与股权质押相结合的运作新模式，坚持理念创新、战略创新、服务创新，积极拓展融资渠道，全力强化内部管理和风险控制，为广大中小企业提供方式新颖、范围广泛、手续简便的担保、投资服务。与此同时，公司通过与多家银行战略合作，储备充裕的资金资源，通过高水平管理运作，建立起担保、投资、典当、咨询为主导的价值链体系，引导资金的合理投向，实现资源的合理配置，为商业银行化解风险，为资本的使用者排忧解难，实现银行、担保公司和中小企业三方共赢的格局。公司业务品种分为担保业务、典当业务、投资业务和咨询服务四大类。担保业务包括财产保全担保、工程担保、对公贷款担保、个人贷款担保、质押担保。典当业务则以车辆抵押贷款、房产抵押贷款、质押贷款为主。投资业务一是运用本公司雄厚的资金优势，对有发展潜力的中小企业进行战略性投资或协助其引进战略投资者，促进其快速成长，二是在企业有短期资金需求时，公司将自有资本金委托商业银行给予短期过桥的支持。另外，公司还为中小企业提供管理、财务、税务以及融资顾问等增值服务，从各个方面帮助中小企业提高自身素质。天津永信投资担保有限公司拥有一支由具有专业知识和丰富实践经验的金融专家、投资专家、理财专家及律师组成的团队，凭借科学的法人治理结构，雄厚的资本实力，强劲的拓展能力，顺畅的银行通道，完备的风险控制体系，卓越的企业文化，打造了具有全国影响、富有地域特色、拥有自主创新的“永信”投资担保品牌，先后为中国建筑第八工程局、江苏南通三建集团有限公司、江苏通州建总集团、中国建筑第六工程局、天津滨海建设集团有限公司、天津滨海浙商投资控股有限公司、天津市塘沽区市政工程局等一大批企业解决了融资问题以帮助其更好更快的发展，永信已成为了一个为客户提供全面金融服务的综合性“金融平台”。新世纪新阶段，党中央国务院从我国经济社会发展全局出发，做出了加快天津滨海新区开发开放的重大战略决策。滨海新区坚持改革开放带动、科技创新引领、高端产业支撑、服务能力提升、发展环境保障，将努力成为继深圳经济特区、上海浦东新区之后服务中国区域经济发展新的增长极，成为科学发展的排头兵。金融服务业作为滨海新区支柱性产业，必将迎来更多的发展机遇和空间。“永信”作为滨海新区投资担保行业的重要成员，将汇聚更多人流、物流、资金流和信息流，着力构筑新的发展优势，做出更多的社会贡献和经济贡献。公司地址：天津市河西区广东路与前进道交口招商银行大厦8层公司网址：www.tedafunds.com', 'www.tedafunds.com');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (249, '湖南棕榈浔龙河生态城镇发展有限公司', '100-499人', '股份制企业', 'https://company.zhaopin.com/CZ265644180.htm', '长沙', '长沙县黄兴大道北浔龙河生态艺术小镇接待中心', '五险一金，交通补助，餐补，通讯补助，带薪年假', '项目介绍低碳、智能、人文相融共生，山水、自然、生活从容乐享。浔龙河生态艺术小镇位于湖南省长沙县果园镇，总规划面积14700亩，总投资150亿元。在2009年之前，该村为省级贫困村。在“归雁”企业家柳中辉的带领下，以党建为引领，对乡村资源、乡村文明、治理结构进行重构和提质。通过推动土地集中流转、环境集中治理、村民集中居住的“三集中”，实现村民的就地城镇化；通过土地改革和混合运营，发展生态、文化、教育、旅游、康养五大产业，让“绿水青山变成金山银山”，推动农民致富增收，逐步实现了乡村振兴“产业兴旺、生态宜居、乡风文明、治理有效、生活富裕”的发展目标。在浔龙河，资本带着温度和感情下乡，进行保护性开发，城中有村，村中有城；闹中求静，动静相宜，让村民诗意地栖居，让乡愁得以保留和延续。如今的浔龙河村已是全国美丽乡村示范村庄，其实践和探索为当前供给侧结构性改革创造了一个农村样板，为农村综合改革创新了可复制的核心路径，为乡村振兴战略提供了理论和实践的支撑。浔龙河生态艺术小镇成为都市近郊型乡村振兴的示范案例。', '');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (250, '中国长城资产管理股份有限公司湖北省分公司', '100-499人', '国企', 'https://company.zhaopin.com/CZ670677480.htm', '武汉', '武汉市武昌区东湖路155号', '', '2019年员工招聘公告中国长城资产管理股份有限公司湖北省分公司（以下简称中国长城资产湖北分公司）是中国长城资产管理股份有限公司在湖北设立的唯一分支机构。中国长城资产管理股份有限公司成立于2016年12月11日，注册资本431.5亿元，由中华人民共和国财政部、全国社会保障基金理事会和中国人寿保险（集团）公司共同发起设立。公司前身是国务院1999年批准设立的中国长城资产管理公司，是中国四大金融资产管理公司之一。目前，公司服务网络遍及全国30个省、自治区、直辖市和香港特别行政区，设有31家分公司和1家业务部；旗下拥有长城华西银行、长城国瑞证券、长生人寿保险、长城新盛信托、长城金融租赁、长城投资基金、长城环亚国际、长城融资担保、长城金桥咨询、长城国融投资、长城国富置业等10多家控股公司，公司致力于为客户提供包括不良资产经营、资产管理、银行、证券、保险、信托、租赁、投资等在内的“一站式、全方位”综合金融服务。公司致力于为客户提供包括不良资产经营、资产管理、银行、证券、保险、信托、租赁、投资等在内的“一站式、全方位”综合金融服务，致力于打造具有国际影响的“百年金融老店”。中国长城资产湖北分公司始终坚持以人为本，注重为员工提供广阔的事业舞台和成长空间，现分公司因业务发展需要面向社会诚聘英才，热诚欢迎各行业有识之士加入发展行列，共创中国长城资产美好未来！中国长城资产湖北分公司本次招聘事项如下：一、招聘岗位职责及条件（一）岗位及职责业务拓展岗：1人。负责客户开发和维护，不良资产、投资投行、并购重组等业务拓展，项目尽职调查、项目实施与项目管理等。（二）应聘人员条件1、综合素质好，工作责任心强，遵纪守法，为人正派，能够承担较重的工作压力，无不良记录。具有较强的团队合作意识、良好的沟通协调能力和语言文字表达、分析能力。2、认可中国长城资产企业文化，忠诚并致力于中国长城资产的发展事业。3、全日制大学本科及以上学历，经济、会计、金融等相关专业，熟悉国家经济、金融政策和法律法规。4、具有银行、证券、信托、会计、法律等行业从业经验、较强的市场营销能力（非应届毕业，具有2年以上工作经历）。拥有大客户资源，具有注册金融分析师、金融风险管理师、注册会计师、注册评估师、律师等资格的优先考虑。5、年龄在30周岁以内。二、用工待遇及用工地点被录用人员享受正式在编员工的薪酬福利待遇。招聘工作地：武汉市。三、报名时间和报名方式1、报名截止日期为2019年4月9日。2、应聘人员登陆中国长城资产管理股份有限公司官网（网址：www.gwamcc.com）下载《应聘报名表》，填写后发送至指定邮箱：hubeihuman2019@gwamcc.com。联系人：沈先生，电话：027-86771230。3、请勿自制表格，勿修改表格结构。4、请以附件形式发送至应聘岗位所在单位的专用邮箱，勿错投，勿重复投递。5、邮件主题及应聘报名表题目请按照“应聘单位－应聘岗位－本人姓名”的格式填写。6、提供个人近期全身生活照电子照片一张，与《应聘报名表》一并发送到指定邮箱。四、相关要求和相关事项1、除网上报名外，不接受其他形式的报名，谢绝来电、来访。2、报名截止日后，我分公司将以电话或短信方式通知符合条件人员进行笔试、面试，请保持通信畅通。笔试、面试具体时间和地点，另行通知。笔试时需提供以下资料供审核：（1）本人有效身份证、学历学位证明、职（执）业资格、专业技术职务任职资格证明等资质原件及其复印件各一份。（2）近期1寸免冠彩色证件照片2张及电子照片。（3）近年来主要获奖证书、工作业绩（成果）材料、相关证明材料等。3、应聘人员对《应聘报名表》内容和提交材料的真实性负责，如与事实不符，我分公司有权取消资格。4、我分公司将妥善保管应聘者个人信息，所有应聘材料恕不退还。Normal
