insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (268, '郑州市金水区中兴酒水商行', '500-999人', '民营', 'https://company.zhaopin.com/CZ494622880.htm', '郑州', '北三环中储股份南阳寨股份分公司', '通讯补助，房补，包吃，五险一金，节日福利', '老村长酒产自中国东北黑龙江，为东北特色低档白酒中的优质品牌。酒质口感柔和��甘甜顺畅，饮后头不痛，口不干，有一种极为舒服的感觉，实属纯粮上乘佳品。老村长酒特选松嫩平原红高粱、糯米、大米、小麦和玉米为原料，得益于亿万年植物分解而成的黑土盛产的金粮，且生长周期超过150天，得益于世界卫生组织公布的世界上最好的、最适宜人类饮用的冰雪岩溢水，集原始酿造工艺及现代技术精酿而成。老村长酒是黑龙江省酒业有限公司生产，该公司是一家大型的白酒生产型企业，产品销售全国各地，在全国各省都设有销售办事处，公司总部坐落在美丽富饶的黑龙江省双城市，年生产能力超过万吨，被评为“黑龙江省著名商标、大众美酒典范、中国优质白酒产品”等荣誉称号。招聘热线： 0371-55180500（郑州市）', '');
