insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (274, '云南金隆伟业电子有限公司', '20人以下', '股份制企业', 'https://company.zhaopin.com/CZ725554390.htm', '昆明', '昆明市盘龙区同德广场', '周末双休，项目奖金，六险，通讯补助，定期体检', '云南金隆伟业电子有限公司于2010年04月成立，是由原云南金隆伟业科技公司的技术中心剥离出来成立的独立法人企业，从事安防和智能交通领域项目集成和相关产品研发。到目前，公司有：计算机软件著作权登记证书16个，发明专利12项，相关的安防资信等级一级证书，电子与智能化工程专业承包二级资质，计算机系统集成肆级资质。公司主要在公安、交警、边防等领域发展，近二年来公司每年的销售收入数千万元，状态良好。', '');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (275, '途家网网络技术(北京)有限公司', '1000-9999人', '合资', 'https://company.zhaopin.com/CZ512796120.htm', '南通-崇川区', '智慧之眼12层', '餐补，带薪年假，补充医疗保险，交通补助，周末双休', '途家，全球公寓民宿预订平台，于2011年12月1日正式上线。作为中国住宿分享的引领者，途家致力于为房户提供丰富、优质、更个性的出行住宿体验，同时也为房东提供高收益且有保障的闲置房屋分享平台。途家现已覆盖国内345个目的地和海外1037个目的地，在线房源超过65万套，包含民宿、公寓、别墅等住宿产品及延展服务，可满足以“多人、多天、个性化、高覆盖”为特征的出行住宿需求。包括但不限于家庭出游、商务差旅、休闲度假、聚会团建和过渡性住宿等各类需求。用户可通过电脑、手机、微信、7×24小时客服电话等多种渠道轻松预订，即刻体验当地人的生活，同时享受这一新兴住宿方式带来的温馨、舒适和便捷。在服务保障方面，途家会对平台房屋执行实地验真，并为房客提供高达1000万元人民币的“安心租”先行赔付保障基金，确保消费者的每一次安心入住。途家欢迎有闲置房屋的房东，来途家分享房源探索全新的房屋分享收益模式和分享社交快乐。途家凭借旗下途家网、蚂蚁短租、携程民宿、去哪儿民宿四大平台的海量用户入口，高效运营及贴心的服务，通过最简便高效的“途家管家”，使房东在免费发布房屋信息、轻松赚钱的同时，还可以与来自世界各地的房客相互交流分享。途家专业的房东保障计划及经营指导，免去房东们的后顾之忧，使分享赚钱更省心。2017年，为了帮助更多的闲置房源发挥价值，途家创行业之新潮，推出了RBA服务，从上房订价，到布草洗涤，利用智能物联，结合全方位的管家业务，为房东们提供了“一键托管，轻松入账”的卓越服务。资本市场方面，随着国内分享经济的逐步升温和途家预订业务的不断增长。2015年8月3日，途家宣布完成D及D+轮融资，总估值超10亿美元，正式进入互联网行业的10亿美元“独角兽”俱乐部。2016年6月6日，途家宣布战略并购蚂蚁短租，进一步强化了住宿分享市场的领导企业优势。2016年10月20日，途家宣布战略并购携程、去哪儿公寓民宿业务，正式形成“途家、蚂蚁短租、携程、去哪儿的公寓入口”四大平台的矩阵。2017年10月10日，途家宣布完成3亿美元E轮融资，线上平台估值超15亿美元。此轮投资由携程与全明星投资基金领投，华兴新经济基金、Glade Brook Capital资本、高街资本跟投。这次融资是继2015年8月完成D轮及D+轮融资后，资本市场对途家的再次加码，而途家也继续保持住宿分享领域单轮最大融资额纪录和行业独角兽的地位。作为中国住宿分享的引领者，途家凭借创新的商业模式、强大的品牌优势和专业的运营能力，目前已与国内218个政府机构签约，并与TOP100的大部分房地产开发企业达成战略合作，签约管理资产超过1500亿人民币，签约储备房源超100万套。加入途家，享受工作的乐趣和创业的收获！如果你喜欢旅游，热爱互联网，善于学习，乐于分享，有出色的解决问题和创新能力，向往和一个有成功经验和创业激情的团队工作，欢迎你加入一起学习、共同成长、成就事业！联系方式：微信号：tujiaHRQQ群：336206148新浪微博：途家招贤馆其他岗位投递网址：http://content.tujia.com/Jobs/Index.htm途家网网址：www.tujia.com，更多关于途家的信息，请登录查询。途家福利：度假式工作、免费班车、拼车补贴、六险一金、途家贡献假、孝亲假、员工专属房源折扣价、生日趴、给亲属的各种礼物等等。如果你在途家工作期间，刚好遇到了你心爱的另一半，那你的福利肯定是大暴天了，除了婚假外，还有专属蜜月房福利哦，免费入住途家自营房5间夜！大别墅、海景房任亲挑选！', '');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (276, '杭州好帮手财务管理有限公司', '20人以下', '民营', 'https://company.zhaopin.com/CZ833620140.htm', '杭州', '杭州市拱墅区万达广场C座', '创业公司，不加班，交通补助，通讯补助，节日福利', '1。公司介绍本公司，属于电销商务平台，公司刚组建不久，因管理中心发展需要，现招聘销售人员数名等。有关事项公示如下。2．需要招聘员工的具体要求年龄：18岁-35岁性别：性别不限学历要求：初中以上岗位职责：销售工作经验：不限上班时间：早9-12点。下午13:30-18:00。周末单双休。一个月最低6天休工作待遇：底薪3000+提成（最高提成40%）转正后底薪3300+提成，交五险一金。职位描述：1、初中以上学历，能力突出者可放宽学历要求，普通话流利，良好的沟通能力2、有望成为顶尖销售高手，收入上不封顶，有望成为优秀组织管理及经营人员3、性格开朗，富有爱心及高度责任感；4、普通话标准，善于交流，性格开朗；5、勇于不断迎接新挑战，能承受较大的工作压力6、具有良好的沟通、协调能力和吃苦耐劳精神。3．面试者请提供个人简历及学历等有效证件，以及应聘流程。有意者请联系人事部王经理安排面试4．面试具体描述：联系人：王经理邮箱：kd1061168200.cm@qq.com电话：13735845967公司地址：杭州市拱墅区万达广场C座', '');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (277, '深圳市东方卓越科技有限公司', '20人以下', '民营', 'https://company.zhaopin.com/CZ265202510.htm', '深圳', '龙岗区坂田杨美地铁站A出口东海王C栋301（杨美地铁A出口）', '五险一金，全勤奖，带薪年假，节日福利，房补', '深圳市东方卓越科技有限公司创建于2012年，专注于跨境电商，主营箱包，电子，玩具等品类。东方卓越紧握机遇领先布局，专注打造世界一流的跨境电商龙头企业。公司以自营跨境电商零售渠道运营为主、第三方跨境电商渠道运营为辅，并通过旗下多渠道开展业务。多种跨境出口模式的发展进一步扩大了公司供应链规模优势。在自营出口零售业务方面，东方卓越通过自建专业品类的电商网络体系，以高性价比的中国制造品牌，为全球用户提供高品质的海量选择，借助互联网技术及大数据，努力打造成为涵盖智能化营销体系、采购体系、物流体系、仓储体系、客服体系的全球跨境电商零售企业。运营模式：东方卓越采用化繁为简业务经营模式，以客户需求为导向，整合上游、深耕中游、服务下游，在提高公司经营效率的同时，压缩流通环节，降低交易成本，力求为客户提供“快、优、惠”的消费体验。凭借资源整合的不断自我赋能、移动端的先发布局、供应链的规模优势、多位一体的销售模式等竞争优势，东方卓越在激烈的出口跨境电商竞争当中稳居领先地位。未来愿景：致力成为全球领先的跨境电商企业，服务全球，享誉世界。顺应中国品牌全球扩张的大趋势，成为跨境电商生态的龙头企业，用信心和实力，助力中国制造，走向世界。员工福利：正式录用的员工，公司将为其提供以下待遇及福利：1、优厚的薪酬及广阔的个人发展空间；2、公司为其购买五险一金，享有全勤奖、餐补、房补；3、在公司服务满一年起享有带薪年休假；员工达到法定结婚年龄，享用婚假；4、公司为每位过生日的员工送上生日祝福红包并在公司举行生日庆会;5、在国家传统的节日里，公司为每位员工送上节日的祝福及礼品；6、公司根据年度整体效益，为做出贡献的优秀员工发放年末红包进行奖励。企业文化：简单、高效、创新、务实、共赢。员工激励计划：公司业务不断扩张，你的钱包也不断丰满。东方卓越从2018年推出“员工激励计划——奖励业绩超标的你”，对于业绩突出的员工，公司给予丰厚的激励奖金！培训体系——多方位助力能力提升：我们经过多年的摸索与创新，循序渐进的形成了具有符合公司发展需求的培训体系与核心岗位后备人才培养机制。并使之贴切行业特点，服务于广大员工在专业技能、专业素养、领导力等方面的系统性提升。职业发展——多层次的双通道发展模式与岗位需求相结合，根据每个人性格特征与个人成长意愿，设立管理/专业双通道，不同通道之间可以较为灵活的转换。充分尊重每位员工的发展，为企业与个人创造双赢局面。管理晋升通道：员工→主管→经理→高级经理→部门总监→事业部总监→总经理专业晋升通道：助理工程师→初级工程师→中级工程师→高级工程师→专家→资深专家→首席专家欢迎加入跨境电商行业，与公司一起努力打造更加辉煌的明天。公司网址：www.katherleen.com', '');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (278, '深圳市英卓体育文化传播有限公司', '20人以下', '民营', 'https://company.zhaopin.com/CZ829791150.htm', '深圳', '龙岗区罗岗路', '五险一金，包住，全勤奖，加班补助，员工旅游', '深圳市英卓体育文化传播有限公司成立于2018年，旗下”乐动超人少儿运动馆“主营体适能培训（少儿+成人）、健身工作室、社区团操业务；目前“乐动超人”首推少儿运动馆及家庭健身私教业务，在传统成人健身的基础上，打造”少儿体适能+父母健身私教“同时进行的新健身模式。目前的中小学生，由于学习压力大，运动时间少，亚健康状态偏多，免疫力不佳的现象比较突出。乐动超人少儿运动馆是专为2-15岁青少年儿童量身订做的体适能品牌，主要面向青少年儿童及其家人提供适度的体适能训练、体质改善、减脂塑形、运动增高、营养指导、运动托管、亲子活动、户外拓展等一系列全新业务。我们旨在通过以上活动，对家庭成员的健康进行有系统、有计划、有跟进、有监测、有效果的循环管理，为家庭特别家庭寄予厚望的孩子提供丰富而健康的生活方式导入。乐动超人以体适能为训练基础，以科学、全面的运动课程呈现，在提高孩子体质、体能的前提下，充分关注孩子在运动过程中养成开朗、坚毅的品格；丰富有趣、逐步进阶的的课程体系，让孩子在快乐运动的同时，促进大脑发育，提升个体综合能力！', '');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (279, '杭州厦旅国际旅行社有限公司', '20-99人', '国企', 'https://company.zhaopin.com/CZ650216380.htm', '杭州', '东新路840号3A18', '绩效奖金，年终分红，全勤奖', '杭州厦旅国际旅行社有限公司是由厦门厦旅国际旅行社控股的国资委旗下的投资公司。主营华东地接，组团，入境，福建专线，出境，游轮等各项综合旅游业务。厦门旅游集团是市委、市政府委托市旅游局管理，唯一以旅游为主业的综合性国有集团公司，公司注册资金为1.25亿元。集团公司以旅行社、酒店、旅游运输、景区景点开发、旅游文化创意等为主业，兼营进出口贸易、仓储物流等。集团旗下拥有厦门鹭江宾馆、厦门海景千禧大酒店、厦门厦旅国际旅行社有限公司、厦门厦旅商业运营管理有限公司、厦门厦旅文化产业有限公司、厦门旅游集散服务中心、香港康祥旅游有限公司、厦门旅游培训中心、厦门友谊交通公司、厦门三联船务企业有限公司等15家全资和参（控）股企业。', '');
