insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (305, '平安普惠投资咨询有限公司昆山黄河路分公司', '10000人以上', '上市公司', 'https://company.zhaopin.com/CZ332761330.htm', '昆山', '昆山开发区现代广场A座', '五险一金，带薪年假，弹性工作，定期体检，高温补贴', '平安普惠金融业务集群（以下简称“平安普惠”）隶属于中国平安保险（集团）有限公司（以下简称“中国平安”、“集团”），以创新的科技，和卓越客户体验，为广大个人和小微型企业提供专业的贷款服务，助力中国实体经济发展。2005年中国平安于深圳开启个人消费金融业务，2007年成立信用保证保险事业部，创立"保证保险+银行贷款"的业务模式，帮助个人及小微企业客户获取无抵押贷款，2010年创立平安直通贷，开展贷款产品的线上销售服务，2011年成立陆金所发展p2p业务，2014年收购富登融保，拓展小微企业市场。2015集团整合平安信保、平安直通贷款业务、陆金所辖下的p2p小额信用贷款业务及富登融保业务，成立平安普惠金融业务集群，专注解决个人和小微型企业的消费金融需求。平安普惠历经十年发展，在综合实力、公司治理等方面已成长为金融行业专业典范。卓越的国际化管理团队，拥有丰富的管理经验；专业的风险管理团队，通过引入远程面谈、大数据客户评分、反欺诈扫描等先进技术手段，不断提高风险管控能力，提升审批时效；丰富的无抵押、有抵押贷款产品，以更低成本为普罗大众提供更多优质贷款选择；先进的互联网技术及创新能力，不断提升服务水平，为客户提供愉悦、简单的贷款互动体验；强大的外部商务合作能力，构建起贷款生态系统。目前平安普惠已累计帮助超过200万客户获取超过900亿元贷款，线上交易规模居全国前列。超过4万人的线下销售和服务团队，分布于全国170余个城市近600家网点，线上超过3000人的远程销售服务团队，724小时为个人及小微企业客户提供专业的消费金融服务，帮助客户解决当下问题，提高生活品质，带来美好生活。秉承“每一个客户都是值得信赖的”品牌理念，凭借深度洞察，发掘客户值得信赖的一面，向社会传递“普惠精神“。未来，平安普惠将在平安3.0时代的背景下，通过互联网创新技术，以更加开放的姿态，发挥综合优势，携手全社会、全行业共建普惠金融生态圈，为广大小微型企业和个人客户提供更加优质的贷款服务，打造全球贷款客户信赖的消费金融机构。', '');
