insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (382, '三河市多米来电子商务有限公司', '20-99人', '民营', 'https://company.zhaopin.com/CZ242982880.htm', '廊坊-燕郊开发区', '燕郊开发区天山智谷202栋5层', '包吃，带薪年假，节日福利，绩效奖金，每年多次调薪', '三河市多米来电子商务有限公司成立于2013年，是北京多米来服装服饰有限责任公司的线上销售公司。通过网上在线销售覆盖全国所有城市市场。是全淘宝销量第一的厨师服品牌！旗下有：天猫5店，淘宝3店，京东、阿里、分销、微店等多家店铺，2014年至今是全淘宝销量第一的厨师服品牌。2017年迁入燕郊天山智谷自购3300平电商大楼，以更好的办公环境和积极向上的电商年轻团队让公司业绩逐年上升！', '');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (383, '深圳市远景医疗投资有限公司', '100-499人', '民营', 'https://company.zhaopin.com/CZ577417120.htm', '深圳', '深圳市福田区八卦一路617栋艺华大厦601', '绩效奖金，包吃，包住，餐补，房补', '深圳市远景医疗投资有限公司。经过十几年来的发展，现已成为一所集医疗、物业、贸易、教学培训为一体、实行现代化企业管理的事业单位。下设有相对独立的医疗门诊部、物业管理部和医药职业技术培训中心；门诊部内设中西医内外科、传统医疗康复保健科、生殖康复科、妇科、口腔科及检验、放射、 B超、心电图、等医技科室。拥有一批高年资、临床经验丰富的卫生专业技术人才，设置有舒适、优雅的就诊环境，并在2001年深圳医疗体制改革中率先试行医药分家，深受广大患者的信任和欢迎。在做好以医疗为龙头的业务基础上，本中心积极参与各种医疗研讨培训课题，承担着卫生系统在职医疗技术及管理人员的再教育培训任务，曾多次成功举办医疗专业研讨会及培训班，成为深圳市医疗卫生教育培训基地之一。我们将秉承客户至上、服务第一的精神，继续为广大市民提供优质的服务。公司具有雄厚的资本实力及一流的经营管理团队，拥有一支由资深医院管理专家、海外学成归国MBA等组成的经营管理团队，坚持以服务为核心、以病人为中心的服务宗旨。公司致力于管理、技术和人才等核心竞争力的塑造和可持续发展，以前瞻性的眼光和革命化的创新模式，树立企业文化，担负社会责任，在医疗健康产业开拓进取。公司推崇“以人为本”的原则，为每一位加入者，提供施展才华、责权利结合的科学发展平台，以及人性化、规范化的管理制度，让每一位员工都能人尽其才。', 'http://www.yuanjings.com/');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (384, '深圳健安医药有限公司', '1000-9999人', '股份制企业', 'https://company.zhaopin.com/CZ121011280.htm', '深圳-福田区', '.', '五险一金，带薪年假，绩效奖金，通讯补助', '深圳健安医药有限公司是一家医药代理销售公司，主要是通过临床终端进行学术推广、学术营销。是目前国内医药销售企业中规模最大、运营时间最长的、最专业化的终端促销服务商。我们的经营模式是：独家经营、学术营销、广布售后服务网络。健安成立于1985年10月，2000年11月公司改制，改为非国有股份公司，2003年按《公司法》要求引进外部股东，公司管理更完善。健安公司在顺应市场环境变化的同时，继续秉承学术推广、学术营销的理念，不断开发有学术价值、有前瞻性的医药产品，大力拓展销售领域、发展壮大销售队伍，创造双赢的医药商业网络，建立稳固的医药商业平台，公司销售规模也逐步攀升至十个亿的目标。多年来健安以总经销、总代理的形式在中国市场独家经销、成功开拓并培育了如瑞士素高药厂的“立芷雪”（止血药，原名立止血）、素高捷疗（针剂、眼膏、软膏、口腔膏）系列（牛血清细胞赋活剂）、由前中日友好协会会长的宇都宫德马先生创建的日本米诺发源制药株式会社的“美能”（复方甘草酸苷）等多个国内外领先的医药和医疗产品，培养了一支学术型临床促销队伍，建立了以北京为龙头、上海为中心、深圳为基地、省会城市为重点的庞大售后服务网络。并以诚信、务实的态度和高效的专业水准与国内外客户建立了长期、密切的合作关系，在国内外医药界获得到了高度评价。目前拥有近200多家由各省市最大的医药公司组成的商业网络，保障着健安商业物流的高效运作，使健安经销的品种覆盖全国90%以上的医院。目前健安代理销售十多个品种，包括应用于临床肝病科、皮肤科的“美能”（复方甘草酸苷）[日本米诺发源制药株式会社生产、卫材（中国）分装]；应用于皮肤科的“奥深”（复方氟米松软膏）[香港澳美制药厂]、童诺（异维A酸红霉素凝胶）[国药集团武汉中联四药药业有限公司]；以及藏药“益心康泰”[青海益欣药业有限责任公司]；中药“小儿牛黄清心散”[山东广育堂国药有限公司]；以及我司首个自有专利品种“博尔宁胶囊”（纯中药抗肿瘤药品）[香港长康堂有限公司独家研制]等。公司还与日本铃谦（深圳）医药有限公司合作，代理销售用于防治骨质疏松症、促进钙吸收的萌格旺（阿法骨化醇片）[帝人制药株式会社]。除此之外，在基础用药市场方面有二十多个基药品种在做推广服务。目前公司将继续从包括日本在内的几个国家引进产品，作为总经销品种用于国内临床市场的销售。健安已成立三十多年，作为国内医药销售企业规模大、运营时间长、专业化强的终端促销服务商，我们培养了一支学术型临床促销队伍分布在全国各地，我们搭建了大区、片区、办事处三个层级的销售网络架构，以大区为管理中心、片区为业务区域从事市场开发、学术推广、售后服务。目前公司拥有10个大区、35个片区、200多个办事处，近1400人的终端销售队伍。健安于2002年11月通过了ISO9001国际质量管理体系认证，建立完善的质量保证体系；作为医药经营企业，公司已按要求于2014年通过了新版GSP（药品经营质量管理规范）认证。健安总部在深圳，办公地点：深圳市福田区上步中路1016号，总机电话：0755-82101886，传真：0755-82101887。', 'http://chinaja.com/');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (385, '深圳市兴嵘建设有限公司', '100-499人', '民营', 'https://company.zhaopin.com/CZ571941430.htm', '深圳', '深圳市福田区梅林路46号天欣大厦319', '五险一金，年底双薪，包住，餐补，周末双休', '深圳市兴嵘建设有限公司是一家以建筑施工为主业的民营企业，总部位于广东省深圳市福田区。公司注册资本2080万元人民币，2018年公司完成产值1.2亿元，现有备案注册人员近167人，其中建造师18人，中级及以上职称人员48人。公司具有市政公用工程施工总承包、建筑工程施工总承包、机电工程施工总承包三个总承包资质，以及建筑装修装饰工程专业承包、城市及道路照明工程专业承包、地基基础工程专业承包等11个专业承包资质，并通过 ISO质量标准认证。公司尊重员工，珍惜人才，“容其所短，更能容其所长”，真诚为每位员工搭建一方施展才华的平台，让“以人为本”的核心价值观成为公司拓展事业的坚实动力，真正实现“永续的发展和超越”。', '');
