insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (496, '深圳市华誉教育研究发展中心', '100-499人', '民营', 'https://company.zhaopin.com/CZ620240330.htm', '深圳', '深圳市龙岗区中心城碧湖玫瑰园17栋208商铺', '五险一金，绩效奖金，年终分红，带薪年假，员工旅游', '深圳市华誉教育研究发展中心是一所定位高端、理念先进、特色鲜明、名师云集的民办教育研究发展中心。中心的创办人是《教育》杂志的封面人物；中国教育学会中小学语文高质量阅读研究中心主任；深圳市名师；全国著名的名师和青年教师培养的导师，近几年为全国各地培养过数千名省市及全国名师、名班主任。中心聘请有北大的思维博士、北京师范大学思维中心首席专家、留美的思维研究博士、大学教授、全国知名校长等高端专家组成思维课程开发研究核心团队。中心业务主要有三个板块：教师培训、学生培训、家长培训。地址：深圳市龙岗区中心城碧湖玫瑰园17栋208商铺。', '');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (497, '河北中测计量检测有限公司', '20-99人', '民营', 'https://company.zhaopin.com/CZ445953680.htm', '石家庄-桥西区', '红旗大街333号河北老年大学院内', '五险一金，绩效奖金，餐补，员工旅游', '河北中测计量检测有限公司于2016年10月成立，主要从事计量校准、检测、计量技术开发及咨询服务。是一家集检测、校准、检验及技术服务为一体的综合性第三方机构，在全国范围内为企业提供一站式校准服务。公司现有四个专业实验室：几何量实验室、力学实验室、热电实验室、化学实验室，主要开展计量仪器设备的校准服务工作。我们将向着朝阳行业和国际化的目标迈进并期待与您共同努力，全力服务于企业，为企业提供准确的量值数据。我们秉承行为公正、管理科学、数据准确、经营诚信的质量方针，打造一支训练有素、装备一流、团结奋进的团队！让我们与公司一起成长，我们期待您的加入！', '');
