insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (501, '中科星图股份有限公司', '100-499人', '股份制企业', 'http://special.zhaopin.com/pagepublish/14271011/index.html', '北京', '顺义区临空经济核心区机场东路2号国家地理信息科技产业园1A-4 星图大厦', '五险一金，绩效奖金，餐补，弹性工作，补充医疗保险', '中科星图股份有限公司成立于2006年，是由中国科学院电子学研究所投资的国有控股高新技术企业。公司秉承“创新、融合、奋斗、幸福”的企业价值观，以“天眼感知世界”为使命，致力于成为全球领先的空天大数据系统与服务的提供商。公司主营业务是面向国防、政府、企业、大众等用户提供数字地球产品和技术开发服务。数字地球是大数据、云计算和人工智能等新一代信息技术、地理信息技术与航空航天产业深度融合构建的数字化的地球。它以空天大数据为基础，承载国防、自然资源、交通、气象、海洋、环保、应急等众多行业应用，是面向“数字经济”、“智慧城市”、“精准扶贫”等国家重大需求的重要新兴软件平台。公司的主要产品包括GEOVIS数字地球基础平台产品和GEOVIS数字地球应用平台产品。GEOVIS数字地球作为公司的核心产品获得北京市科学技术奖一等奖、军队科技进步一等奖、军队科技进步二等奖、军队科技进步三等奖。公司是中国软件行业协会、中国指挥与控制学会、中国卫星全球服务联盟、中国遥感应用智慧产业创新联盟、中关村国科航天产业技术创新联盟的副理事长单位，是中关村高新技术企业协会常务理事单位、中国地理信息产业协会理事单位，通过与行业协会的交流互动，大力促进了数字地球平台生态化发展。此外，公司获得北京市级企业科技研究开发机构、北京市国民经济和装备动员重点单位、2018十大创新软件企业和十大软件创新产品、2018年度中国空天大数据领域影响力企业、2018中国软件行业最具影响力企业、2018“军工榜”年度十大商业航天企业等奖项。', 'http://www.geovis.com.cn/');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (502, '山西晋胶健康产业有限公司', '20-99人', '民营', 'https://company.zhaopin.com/CZ477701230.htm', '太原', '汾河景观360', '带薪年假，节日福利，五险一金，周末双休，交通补助', '山西晋胶健康产业有限公司始建于2006年4月，是一家集养殖基地、科研开发、生产制作、市场销售为一体的大型阿胶类食品有限公司，是山西省阿胶生产企业的佼佼者，全省重点扶持的农业产业化龙头企业。山西晋胶健康产业有限公司生产基地位于榆社县云竹镇东庄食品工业园区，依靠太行山脉和榆社紫金山泉得天独厚的自然环境建立天然养殖基地，为产品提供充足优质的阿胶原料，通过整皮、化皮、熬制、蒸煮、晾晒、切片等50多道全为纯手工操作的传统制作工艺完成纯天然阿胶制作；为扩展公司产品机构化、开发产品多元化，公司建立多条标准化生产线，目前公司旗下有“固媛”阿胶糕、“我很傲娇”阿胶糕系列、胶原蛋白饮系列、晋胶阿胶枣、阿胶山楂系列等休闲阿胶食品，公司产品已通过国家食品药品监督局的SC认证、进出口检疫局出口卫生备案、国际质量管理体系ISO 9001认证、国际食品安全管理体系HACCP认证。山西晋胶健康产业有限公司公司办公地点位于小店区晋阳街英语周报大厦11层。2017年，公司产品迈出国门，销往东南亚各个国家和地区，公司以“争创中国驰名商标和世界知名品牌”为目标，以“东有东阿、西有晋胶”的战略思维，布局全国销售渠道，争取与行业知名品牌东阿阿胶并驾齐驱，发扬山西榆社阿胶文化。', '');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (503, '舒朋士环境科技(常州)股份有限公司', '100-499人', '股份制企业', 'https://company.zhaopin.com/CZ672324420.htm', '常州', '常州市新北区锡山路19号', '五险一金，加班补助，全勤奖，带薪年假，员工旅游', '舒朋士环境科技（常州）股份有限公司是一家专业的智慧排水系统解决方案提供商。作为国内较早推广、专业生产、规模较大的一体化预制泵站制造商，舒朋士秉承“服务客户追求卓越求是创新以人为本持中守正”的经营理念，潜心研究市场，创新产品研发。自2013年在上海成立中国代表处以来，相继成立中国工厂及研发中心，后在浙江、上海、安徽、福建、广西、成都、北京等20多个省市成立办事机构。舒朋士中国工厂2014年于江苏常州建立，工厂占地30亩，预制泵站装配车间达10000平方。具有独立的泵站设计开发测试、不锈钢焊接加工、电气控制系统自主研发及制造等完整的产品解决方案能力。舒朋士先后成功通过了ISO9001、ISO14001及OHSAS等多项管理体系认证；电气控制系统通过中国国家强制产品认证获得3C证书；从2013年起，公司一直与清华大学环境学院、江苏大学建立紧密的产学研合作。2016年，舒朋士成功入选住建部《海绵城市建设先进适用技术与产品目录》和《建设行业科技成果评估》，是行业内为数不多的获得该双项殊荣的公司。2017年公司获得住建部《绿色建筑节能产品》和《工程建设推荐产品》证书。同年获得高新产品及高新技术企业荣誉。2018年舒朋士获邀参编一体化预制泵站绿色建材行业标准。公司开展规范、标准、精细、信息化管理，获得多项管理体系认证自主研发并申请获得了3项发明专利20项国家专利和2项软件著作权，是行业中拥有较多该产品专利的公司。舒朋士是国内较早推广、专业生产规模较大的一体化预制泵站制造商。舒朋士在一体化预制泵站的基础上先后研发Supumps 4.0、CPC、Micro、一体化泵闸、轴流泵站等系列产品，现已成功应用于黑臭水体治理、海绵城市和新农村建设，在滨河带污水截流、城市雨污水管网提升、道路桥梁立交排水、中水提升、取水泵站、新农村排水管网整治、大型水环境综合整治等场景中发挥关键作用。截止目前舒朋士业绩覆盖中国华东、华北、华南、西南等多个片区。舒朋士坚守品质底线，推动行业健康、有序发展，以丰富的泵站设计经验结合实践积累为客户提供优秀的泵送解决方案。舒朋士业绩逐年增长，先后完成了全球大流量的预制污水泵站——扬州仪扬河污水泵站、15米深一体化泵站——北京平谷泃河新城段截污治理工程一体化泵站、亚洲规模较大机场北京新机场项目一体化泵站、昆山农村生活污水治理工程污水处理一体化提升泵站、常熟市农村生活污水治理工程、上海浦东机场一体化泵站、上海崇明岛（长江口北支新村沙）一体化污水泵站、广州佛山东西片区泵站、安徽安庆体育中心泵站、南通市海安高新区钟庄路泵站、浙江象山县松景兰污水泵站、长兴县城区水系统整治污水纳管改造工程、广西钦州市浦北张黄镇污水处理厂及配套管网工程、南宁马山县人民医院直排口截污治理一体化泵站、辽宁大连红星海项目3号一体污水提升泵站、北京朝阳青年汇小区污水泵站、福建厦门集美区道路污水截污工程、海南恒大海花岛排污提升泵站等遍布全国五百多个项目。舒朋士人秉持极致、创新、包容、感恩的价值观，以构筑生态水环境为使命，坚持创新、梦想与责任，为成为水行业一流的系统解决方案提供商砥砺奋进！', 'www.supumps.com.cn');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (504, '深圳市安帕尔科技有限公司', '20-99人', '民营', 'https://company.zhaopin.com/CZ572741520.htm', '深圳', '深圳市龙华区观澜街道环观南路金雄达科技园A栋第六层', '', '深圳市安帕尔科技有限公司是一家集研发、生产、销售、工程和服务于一体的高科技公司。自公司成立以来一直专注于气体检测、气体分析行业。公司拥有36年的气体检测及分析产品开发经验。产品使用遍布全球各地，主要应用于大气环保、航天、军工、医疗、化工、石油天然气、电力、矿业、农业（畜牧业、种植业、保鲜等）、通讯等领域。多年来公司研发团队通过不懈的努力帮助了很多客户突破了许多气体检测中的难点，为国内外气体检测行业的发展做出重要的贡献。公司的产品和服务也赢得了广大客户的认可和赞誉。公司正式成立于2011年，这么些年来安帕尔始终把“成为人类生命健康及财产安全最可靠守护者，成为员工最温馨坚实的港湾”作为企业的使命，并且坚定的把“成为气体检测及分析产品世界品牌”作为我们企业的发展愿景，以公司“五心”经营理念的指导下。在有30多年行业经验的专家团队的带领帮助及其它前辈员工们的努力下。在短短几年里我们公司取得了很多辉煌的成就。公司的“五心”发展经营理念：创心、专心、诚心、贴心、善心。1、创心——以创新的精神打造企业的灵魂。创新是企业的灵魂。创新的基础来源于研发的实力。所以研发是企业发展，品牌塑造的根基，优秀卓越的研发能力能够促进企业向更高层次发展！作为国家级高新技术企业，公司始终把技术创新作为推动公司发展的主要动力，并投入大量资金和人员进行技术研发和创新。目前，公司拥有优越的研发环境、各种专业的气体检测产品研发设备，同时，公司拥有一支专业的富有发明创造力的一流研发团队，促使公司在产品研发过程中能够保持不断创新突破。2、专心——以专业、专注的心态打磨产品。产品是安帕尔发展的生命。公司一直坚持的质量方针是质为本，客为先，创名牌！多年来，安帕尔建立了完善的产品质量管理体系，在严格遵循公司产品质量合格标准的基础之上更加规范和完善公司产品的整个生产流程，公司拥有先进的生产线，认真严谨的生产工人对产品的质量严格把关，通过不断提升品质标准，使安帕尔的产品保持着行业领先的质量和稳定性，靠实力和诚信，打造最精美、最优质的产品，坚持与时俱进。3、诚心——以诚信、共赢的理念经营市场。4、贴心——以陪伴式的方式服务于客户。5、善心——以慈善之心回报社会与家庭。在秉承以上发展理念的同时公司大力实施自主品牌战略，推进商标战略的实施。公司重视产品质量、增强产品和服务，开拓提高产品和服务的附加值，不断加强占领、巩固市场的能力，研发力量与水平的发展，强化人文关怀与社会责任感，这一系列举措带动公司向持续、稳定、健康方向发展。过去的岁月里，安帕尔砥砺前行，诚信务实，锐意进取，不断创新，用自己的睿智和辛劳，以富有责任感的使命，在探索中追求，在追求中超越，致力于成为最值得信赖的气体检测企业！在未来的日子里，安帕尔将一如既往豪情满怀，信心百倍，携手并肩，执着追求，在新的起点上铸造新的辉煌！热情的安帕尔人欢迎广大朋友客户来公司参观、交流学习和指导工作！让我们一起为气体检测行业的发展尽一份力，为人类生存环境的发展做贡献，推动人类社会的发展！公司地址：深圳市龙华观澜环观南路金雄达科技园A座6楼站台：4号线清湖地铁站转清轨站台：东奄或世纪广场；公交站台：观澜天虹（公司在天虹对面）公司地理位置生活便利：科技园区配备食堂、周边房租便宜，饮食一条街。', 'www.empaer.com');
