insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (518, '深圳市大润科技有限公司', '20-99人', '民营', 'https://company.zhaopin.com/CZ569318020.htm', '深圳', '宝安区沙井镇芙蓉六路2号1号厂房', '绩效奖金，包住，全勤奖，节日福利，五险', '深圳市大润科技有限公司成立于2004年7月(原深圳市凯盛绝缘材料有限公司)，公司位于有改革开放试验田之称的深圳经济特区西部工业重镇—沙井街道办，公司秉成“客户至上、诚信首先、服务第一、持续改善”公司方针理念为客户服务；以“建立、建全、完善成一流品保体系；改善、改进、执行成一流品保质量；协调、沟通、联系成一流售后服务！”的品质政策理念为客户提供品质服务！公司是广东省专业生产PET三层绝缘线的厂家，公司自主研发成功的DRTIW-B、DRTIW-F系列的三层绝缘线，由公司聘请国内著名新材料专家研发成功，产品质量达到国内、国际先进水平，部分技术填补国内空白，其中DRTIW-BDX、DRTIW-BBX(注：多芯三层绝缘线、扁芯三层绝缘线)为国内首创，产品拥有自主知识产权！产品广泛使用在各种电源变压器、通讯变压器、电感、线圈、马达、火牛等产品中，为之不可缺少的材料，而各种电源变压器、通讯变压器、电感、线圈、马达、火牛又为电脑、手机、彩电、冰箱、空调、摄像机等等消费性电子、电器必须之元器件，所以用途及为广泛！公司产品有通过UL（E335841）安规认证、VDE(40032470)安规认证，绝缘系统（E349174）“SGS”等安规认证！环保符合欧盟ROHS指令、REACH要求、无卤素、SONYSS-00259等标准！公司产品质量稳定、性能过硬、规格齐全（注：三层绝缘线规格0.10---1.00mm，）、技术先进，目前产能达5.0亿米以上每年，公司目标是打造成中国最大的三层绝缘线生产基地。从而更好的为广大代理商、客户提供更好、更多、更优服务。分公司：安顺市乾辰谷材科技有限公司系由深圳市大润科技有限公司在安顺市西秀区经济开发区投资而成立。', '');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (519, '美克国际家居用品股份有限公司', '1000-9999人', '上市公司', 'http://special.zhaopin.com/2016/tj/mkjj022458', '杭州-江干区', '杭州市江干区富春路308号华城国际发展大厦', '五险一金，绩效奖金，餐补，通讯补助，带薪年假', '美克国际家居用品股份有限公司美克国际家居用品股份有限公司（以下简称“美克家居”），已成为一家多品牌、多渠道和一体化的国际综合家居消费品公司。美克家居的前身是美克国际家具股份有限公司。公司于2000年11月27日在上海证券交易所成功上市，股票简称“美克股份”，股票代码“600337”。美克国际家具股份有限公司于2014年7月25日完成了工商变更登记相关手续，公司名称正式变更为“美克国际家居用品股份有限公司”，公司证券简称自2014年8月1日起变更为“美克家居”，证券代码保持不变，仍为“600337”。公司依托家具制造业走向国际市场，并成为目前我国最大的家具出口企业之一。公司拥有规模化、专业化的加工生产基地、高效的企业管理平台、实力雄厚的研发机构及覆盖全球的销售网络。为了更好的支持多品牌渠道，发挥垂直一体化优势，公司正积极实施家具生产自动化项目，项目建成后将取代大量人工作业，实现工厂自动化，从而提高劳动生产效率，进一步提升产品品质和柔性制造能力。公司已于2009年成功收购了在美国业界有着六十多年历史并广受赞誉的Schnadig公司，2010年完成了对美国A.R.T.家具公司的收购，建立了自己的国际营销渠道，拓展国外市场，并为国内零售品牌提供研发设计支持。2002年1月，公司与控股股东美克集团一起成立了美克美家家具连锁有限公司（以下简称“美克美家”），创立了自己的家具零售品牌—“美克美家”。同时联手美国著名家具零售品牌“伊森艾伦”，共同将国际领先的家具零售模式带到中国。美克美家致力于为消费者提供优质服务，打造卓越客户体验。美克美家位列2013年中国最具价值品牌500强排行榜第438位。目前，美克美家已在全国40多个大中城市开设了近100家自营连锁店，店面的战略布局正在有计划地进行中。为适应公司由零售商向品牌商的转型，打造卓越的客户体验，拓展一体化的家居综合消费品市场，美克家居在2014年全面启动零售多品牌战略，以实现公司转型成为多品牌、多渠道和一体化的国际综合家居消费品公司的战略目标。目前已推出的四个新品牌分别为A.R.T.、ZEST、YvvY以及美克美家旗下子品牌Rehome。其中，A.R.T主要推广纯正美式生活方式；ZEST则更多瞄准第一次置家的年轻人，特点是活泼有趣多彩，更讲究性价比；YvvY则面向追求独特创新的消费群体；Rehome则主要布局于高端时尚的购物中心，为消费者提供精美的家居饰品。', 'http://www.markorhome.com');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (520, '深圳市盛世明德教育管理有限公司', '100-499人', '民营', 'https://company.zhaopin.com/CZ434926630.htm', '深圳', '福田区八卦岭541栋汉富中心2楼盛世明德教育', '节日福利，弹性工作，带薪年假，五险一金，出国游', '盛世明德教育是一家专注于成人学历教育、人才引进服务、小语种培训等为一体的大型综合教育机构；在珠三角地区教育培训领域处于领先水平，是深圳较早专注于从事教育领域的领先机构，其骨干人员均较早长期从事教育工作，对教育行业深有领悟，与国内多家高校——华中师范大学、北京师范大学、陕西师范大学、电子科技大学、湖南中医药大学、天津理工大学、东北财经大学、江南大学、中国地质大学（北京）、南开大学、湘潭大学、西南大学、郑州大学、北京科技大学、西南科技大学、中央广播电视大学（国家开放）等知名教育学校建立了战略合作，运用超前的发展理念，领先的发展战略，良好的发展基础，为企业和个人提供全方位专业的教育咨询服务。目前旗下拥有明德教育、明德咨询、世说新语三个子品牌,两家子公司，业务涉及到广州，深圳，佛山，东莞、成都等城市，在以上城市均设置多家分校。业务内容涵盖学历教育，职业教育、语言教育和入户、留学咨询业务四个模块，到目前为止团队累积已帮助约三万多人提升职业能力。盛世明德教育以“社会所需为导向，以市场需求为标准”在多个省市设有直营分校和加盟分校，学员可以根据自身情况，选择就近分校，仅需出示本人身份证即可全国通用。我校各教育培训中心坚持统一的质量标准和用户至上的原则，形成了“团结、勤奋、务实、创新”的良好学风，并创建了全国最大的教育培训门户服务网站，全方位服务于社会、服务于学员，立志于振兴中华教育及培训事业，让更多有求知欲的人才接受最先进的科技文化知识，全力打造中国教育培训第一品牌。■培训使命我们一如既往的注重学术研究，并给客户提供顶尖教育培训。我们要将高品质的学习课程带给我们享誉全球的合作伙伴。我们不断的创新产品和服务，让更多的人受益其中。我们创造你的未来，同时谱写你的成功！', '');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (521, '上海精锐教育培训有限公司', '10000人以上', '上市公司', 'https://company.zhaopin.com/CZ173698110.htm', '杭州-下城区', '下城区延安路528号标力大厦B座6层', '五险一金，带薪年假，弹性工作，补充医疗保险，定期体检', '精锐教育（OneSmartInternational Education Group Ltd.），美国纽交所上市的大型高端多元化教育集团，由哈佛北大精英创立。秉持“学习力成就未来”的使命，致力于提升4~18岁学生的学习力（包括学习动力、学习能力及学习毅力），打造快乐高效的第三课堂。旗下拥有精锐·个性化、精锐·至慧学堂、精锐·国际教育、小小地球STEM英语等多个高端学科及素质教育品牌，以及巨人网校、UUabc、精锐教育|佳学慧等在线教育品牌。创始人是学霸界的网红——“学霸张”，总结自己在哈佛北大的学霸经验，创新性地将“哈佛案例教学法”融入基础教育，并提出学习力概念，遵循“以学为主、以教为辅、主动学习、多元互动”的学习力原则，通过个性化教学并结合最新科技手段，提升学生学习力，助力成就精英未来。', '');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (522, '中安信业创业投资有限公司', '1000-9999人', '民营', 'https://company.zhaopin.com/CZ143447820.htm', '深圳', '深圳市福田区深南大道7028号时代科技大厦9楼', '周末双休，五险一金，年底双薪，餐补，交通补助', '中国小额信贷技术服务行业领军企业--中安信业集团中安信业集团（以下简称：中安信业）下辖深圳市中安信业创业投资有限公司、深圳市中安信业小额贷款有限公司、深圳市中安信业互联网金融服务有限公司、深圳市中安典当行有限公司、深圳中安财富资产管理有限公司、深圳市普惠安创业管理有限公司等专业机构。经过近二十年的发展，业务逐步拓展到小额信贷技术服务、理财服务、创业投资等多个金融服务领域，在金融行业享誉盛名。中安信业主营小额信贷技术服务业务，公司自2003年开始探索免抵押、免担保小额信贷技术以来，经过近二十年的发展，已成为一家专门协助银行等机构为微小企业主、个体户及中低收入人群提供快捷简便、免抵押、免担保小额信贷技术服务的专业现代化小额信贷技术服务企业，由于小额信贷服务技术是一项创新性金融产品，中安信业因此荣获深圳市政府颁发的2008年金融创新奖，并成为深圳小额贷款协会会长单位。公司立足深圳，面向全国发展，现有近百家分公司遍布全国各地，其中近30家分布在深圳，近70家分布于全国17省34市。公司至今协助银行等机构的累计放款数额、客户数量、营业网点数量位居行业前茅，已成为国内小额信贷技术服务行业的领军企业。中安信业始终相信企业的生存发展是以成功的人力资源管理为基础。为此，集团建立完善的人才选拔、培养、晋升和留用管理体系，并严格执行国家劳动法律法规，保障员工的劳动权利，将员工的努力和公司的发展目标紧密结合在一起，以实现共同的进步和发展。员工一经录用，公司除提供有竞争力的薪酬，愉快、谦恭、互相尊重的工作环境外，还享有以下待遇：1、公司以内部培养人才为主，为员工提供广泛的培训，大力开发和培养小额信贷技术服务人才，使我们的员工不断提升专业技能和职业素养，并为我们的客户提供快速简便、亲切友好、令人满意的服务。2、公司快速发展，为员工提供广阔的职业发展空间和可持续发展的事业。3、带薪年假，中餐补贴、交通补贴、移动话费等多种补贴。4、养老、医疗、工伤、失业等社会保险，意外伤害、意外医疗、交通意外、重大疾病、住院津贴等商业保险。集团地址：深圳市福田区深南大道7028号时代科技大厦9楼集团网址：http://www.zac.cn乘车路线：公交路线328路、329路、324路、370路、245路、54路、44路、21路、222路、204路、101路、223路等于“招商银行大厦”站下车；搭乘地铁请在“车公庙”站B出口下车（请从大堂西侧电梯至9楼）', 'http://www.zac.cn');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (523, '施强集团', '1000-9999人', '合资', 'https://company.zhaopin.com/CZ583728220D00112172623.htm', '杭州-滨江区', '浙江杭州滨江区滨兴路1168号', '节日福利，五险一金，交通补助，餐补，通讯补助', '施强集团成立于1995年2月，总部坐落于杭州国家高新技术产业开发区（滨江），占地面积3.3万平方米，建筑面积16万平方米。业务领域涉及医药、教育、出国中介等，拥有中外高素质员工3000余人，其中中共党员516人。在国内外设有266个办事机构和分公司，是国家高新技术企业，杭州市非公企业“发展强、党建强”双强百佳企业，杭州高新区重要税源企业之一。核心文化:勤奋、务实、追求卓越、视工作为娱乐。用人标准:勤奋、执着、激情、大度。经营理念:至臻品质、至善服务。公司战略:产品战略、渠道战略、人才战略、品牌战略、信息化战略。公司愿景:成就亿万人健康、成就精彩人生。【施强药业】施强医药产业发展于1995年2月，集药品的研发、生产和销售为一体。施强药业全资拥有五家子公司，一家控股子公司。在中国大陆（除西藏外）各省市、自治区、直辖市设有216个办事处，拥有员工2000余人。其现代化生产基地位于中国杭州国家高新技术产业开发区桐庐园区，占地面积11.3万平方米，建筑面积12万方，设计年产能超百亿元。药业主打产品——复方玄驹胶囊拥有自主知识产权、国内独家生产、国家中药保护品种，市场占有率稳居国内同类药品之首，是中国泌尿男科知名品牌。新产品山海丹颗粒，对多种心脑血管疾病具有良好的治疗效果。抗更年期胶囊和预防、治疗抑郁症的知乐胶囊已在国外上市，并获得一致好评。【施强教育】施强教育产业主要业务领域为国际培训教育和智慧教育，拥有专业技术研发人员及专家团队、中外籍教师及员工1000余人。国际培训教育依托浙江施强培训学校，学校成立于2003年7月，专注于外语培训、留学预科、A-Level、TESOL和华文培训，是浙江省TESOL国际英语教师资格证独家合作方，NCC官方授权的预科教学中心，英国剑桥大学国际考试委员会授权的A-Level教学中心，教学管理模式与国际接轨，拥有欧美花园式教学区。智慧教育依托于杭州施强教育科技有限公司，公司成立于2008年2月，主要业务致力于IT技术开发和网络教学的研究。公司拥有高新技术人才200余人，教研团队100余人，营销处50余个。公司自主研发的智慧教育产品——乐课网，是互联网与教育深度融合的产物，已推向全国市场，并受到教育界各级领导、有关学校师生的一致好评。目前合作学校已达800余所，遍布全国各省市。【施强出国】施强出国中介产业发展于1995年2月，是行业内的龙头企业，系浙江省出入境中介服务行业协会会长单位。业务涉及留学、移民、商务考察与培训、国际冬夏令营等。拥有资深教育规划专家、留学专家、移民专家、海外生活顾问等团队，与全球30多个国家的2000余所院校及众多国外政府机构保持紧密合作关系。Normal07.8磅02falsefalsefalseEN-USZH-CNX-NONE/* Style Definitions*/table.MsoNormalTable{mso-style-name:普通表格;mso-tstyle-rowband-size:0;mso-tstyle-colband-size:0;mso-style-noshow:yes;mso-style-priority:99;mso-style-parent:"";mso-padding-alt:0cm 5.4pt 0cm 5.4pt;mso-para-margin:0cm;mso-para-margin-bottom:.0001pt;mso-pagination:widow-orphan;font-size:10.5pt;mso-bidi-font-size:11.0pt;font-family:"Calibri","sans-serif";mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;mso-bidi-font-family:"