insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (526, '深圳中南人力资源有限公司', '1000-9999人', '国企', 'http://special.zhaopin.com/pagepublish/21922531/index.html', '深圳', '深圳市坪山区深圳市坪山区马峦街道坪山大道', '五险一金，绩效奖金，带薪年假，节日福利，包吃', '深圳中南人力资源连锁机构（集团）人力资源业务经过多年发展，已经成为��圳市乃至华南地区最大的派遣公司之一。深圳中南人力资源有限公司.深圳市五星级人力资源服务机构，全国优秀服务机构◆【深圳外企+国企+银行+航空+联合招聘暨大学生就业专场】正在举行！——中南人力资源连锁机构始创于1994年的中南人力资源连锁机构—中国民人力资源行业的先行者和实践者,是深圳市惟一同时拥有“全国优秀职介机构”“五星级职介服务单位”“中国人力资源著名品牌”“全国优秀劳务品牌”“优质服务单位”“人力资源行业典范”“深圳人力资源知名品牌”“深圳十大人才职介机构”“职业介绍信得过单位”“求职者最喜爱的职介品牌等数十项殊荣的大型人力资源连锁机构，是国内优秀的系统人力资源供应商！', 'http://www.znhr.com/AfterInfo/09pmzp/znspecial/index.htm');
insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (527, '南京巨鲨显示科技有限公司', '500-999人', '民营', 'https://company.zhaopin.com/CZ120914350.htm', '杭州', '杭州', '五险一金，绩效奖金，交通补助，餐补，房补', '巨鲨医疗企业介绍巨鲨医疗是一家集团总部坐落于南京的全球性医疗科技企业，旗下有南京巨鲨显示科技有限公司、南京巨鲨医疗科技有限公司、杭州英肯科技有限公司、苏州协朗精密机械有限公司。主要从事先进医疗器械、医疗数字可视化解决方案及“云”手术管理系统及感控清洗、灭菌监测类产品的研发、生产及销售。成立于1996年3月的巨鲨医疗在全球有多个办事处及售后服务点。业务覆盖全球终端医院客户7000余家，其中进入国内三级医院1200家，全球合作伙伴320家。同时巨鲨医用显示器也是唯一入选2008年奥运会医疗中心的国产医用显示诊断设备。根据第三方专业机构统计数据，2016年底巨鲨医用显示器在中国大陆地区市场份额突破60%，这也是本土医疗科技企业在全国能达到的几乎最高的市场份额。巨鲨显示产品已销往全球58个国家和地区，巨鲨医疗同时还是西门子、HOLOGIC等国际巨头的全球合作伙伴，目前巨鲨已经成为全球医用显示领域无可争议的领导者。2013年在全球独家发布了拥有国际专利技术的裸眼3D医用显示解决方案，几年来已应用于多家知名医疗机构。2015年我们又推出了一体化手术室解决方案和数字X光系列放射设备，在多个先进医疗设备专业领域不断有“领跑”产品推出。巨鲨医疗拥有占总人数30%的高端研发团队，团队成员包括一批中青年科学家、博士、硕士等科研人员，是国内在医疗专业领域中研发实力最强的一支高科技人才队伍。巨鲨公司每年在研发体系投入年销售收入的15%以上的资金，成效显著，公司目前已申请国际和国内知识产权400余项，仅生产和制造的巨鲨牌高分辨率医用显示器，就在填补国内技术空白的同时为国家节约了数亿元的外汇资源，并加速推动了国内医用信息化的进程。巨鲨在医疗感控领域也有着自己独特的地位，在该领域也拥有多项令人瞩目的国际国内专利，其中自主研发、生产的五类爬行监测卡居世界领先水平。公司坚持每年举办一次“放射界敬老联谊会”，弘扬敬老爱老传统美德，目前已成功举办22届，是中国放射领域最重要的活动之一。长期以来公司一直尽己所能、奉献社会，将“出色的履行公司的社会义务”作为我们的企业宗旨之一。成立之初，便与省抗癌协会等部门联合举办“抗癌明星表彰大会”，资助经济条件困难的癌症患者，公司还资助贫困地区失学儿童，设立不发达地区先天性心脏病患者医疗救助基金；2008年汶川地震，公司以各种形式持续对受灾地区进行援助达五年之久。近几年公司已在医疗图像的云平台、手术辅助机器人、大型放射设备、感控领域的生物监测、电子监测判读方向加大研发投入。巨鲨医疗将继续在医疗领域耕耘并以开发更加便捷实用的高科技医疗产品、最大程度解除病人痛苦为己任，把巨鲨发展成为全球知名的医疗高科技企业，努力实现巨鲨人为人类健康事业而奋斗的终极目标。', 'cn.jusha.com.cn');
