insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (613, '武汉理工大学深圳研究院', '20人以下', '事业单位', 'https://company.zhaopin.com/CZ859072310.htm', '深圳', '南山区高新南四道19号虚拟大学园', '五险一金，周末双休', '武汉理工大学深圳研究院是武汉理工大学在深圳建立的事业法人单位，我们充分发挥武汉理工大学的资源和人才优势，服务深圳科技和企业发展。开展并加强在材料、海绵城市等领域的研究创新，开展高层次人才培养、国际交流和科研成果转化。武理深研院将围绕深圳市政府“十大行动计划”和“七大战略性新兴产业”，融合武汉理工大学“建工建材、交通物流、汽车制造”三大学科优势，与学校25个学院及多个国家重点实验室（工程中心）紧密结合，重点开展“新材料新能源、建工建材、海洋工程、交通物流、船舶汽车制造、循环经济及产业金融”等领域开展重大应用技术的产业化与成果转化等工作，为深圳“现代化国际化创新型城市”建设与社会发展提供科技与人才服务。', '');
