insert into CompanyInfo (CompanyId, CompanyName, CompanyScale, CompanyNature, CompanyNetAddr, CompanyCity, CompanyAddr, CompanyWelfare, CompanyInfo, CompanyRealNetAddr) values (761, '河南安强科贸有限公司', '20-99人', '其它', 'http://special.zhaopin.com/pagepublish/30090491/index.html', '郑州-金水区', '郑州市金水区经三路东风路交叉口金成国际6号楼西塔2201室', '五险一金，绩效奖金，年终分红，带薪年假，弹性工作', '河南安强科贸有限公司座落在郑州市金水区东风路经三路金成国际，是一家专业从事国内外教学科研仪器设备、实验实训室代理、销售贸易及服务型公司。现公司代理上海曼恒数字技术股份有限公司的VR虚拟现实等核心产品和新道科技有限公司的管理软件、云服务等。曼恒数字是国内VR虚拟行业的领军企业、全球范围内最早从事VR技术研究的企业之一；新道科技是亚太本土最大管理软件、中国领先企业云服务提供商之一。公司成立十几年来，已成功为省内各大高校和各类职业学校提供各类实训室设备集成解决方案案例近万次，与学校定制化联合研制开发专业教学平台近百次，均取得客户的很好反馈，产品与服务涉及机电、光伏、计算机、艺术与工业设计、环境、医学、经管等专业学科，积累了丰富的高校科研和实践教学环境建设的经验。公司企业文化愿景：成为一家受员工、客户尊敬的五十年企业；使命：造富员工幸福下一代夯实基础；价值观：简单高效务实有爱。经过多年的艰苦创业和服务的不断升级，公司现决定扩大公司规模，诚邀精英人士共同打造未来教育之路！公司所处行业背景：世界教育组织统计的相关国家对教育的投入情况如下：发达国家对教育的投入占GDP的7-8％甚至更高；发展中国家对教育投入要占到4％。在近些年国家不断的加大对教育投入的情况下，现阶段，我们国家对教育的投入占比还不到2％，远远低于世界教育组织对发展中国家的定义。国家领导人2016年以来也数次表态，要继续加大对教育的资金投入力度。就目前省内各院校的实际情况，各类教学、实验仪器还有很大缺口，近些年，各院校正在逐年翻倍的加大投入教装设备的采购。这是一个朝阳行业，唯有品行好、肯做长年沉淀、能坚持、有能力、自我约束力强、做事严谨的人才能来掘金（（不满足要求的勿扰））。期待你的加入！', '');
